<script>
$(document).ready(function() {
    $('#results').hide();
    $('#search').on('input', function() {
        var query = $(this).val();
        if (query.length > 0) {
            $.ajax({
                url: 'search.php',
                method: 'GET',
                data: { query: query },
                dataType: 'json',
                success: function(data) {
                    $('#results').empty();
                    if (data.length > 0) {
                        $.each(data, function(index, item) {
                            $('#results').append('<div class="result-item"><a href='+ item.url +'><div class="w-full h-crip cursor-pointer relative"><img class="w-full h-full rounded-meh" src="resources/thumnails/'+ item.thumnail +'" /><div class="flex w-full px-half py-12 absolute bottom-0 rounded-b-meh bg-thumdiv backdrop-blur-xs"><div class="flex flex-col items-start gap-y-se pl-4"><p>'+ item.naslov +'</p></div></div></div></a></div>');
                        });
                        $('#results').show();
                    } else {
                        $('#results').hide();
                    }
                }
            });
        } else {
            $('#results').hide();
        }
    });

    // Optional: Handle click on result item
    $(document).on('click', '.result-item', function() {
        $('#search').val($(this).text());
        $('#results').hide();
    });
});

</script>